import React, { useState, useEffect, useRef } from 'react'
// import usePages from '../data/usePages';

import { Link, useLocation } from "react-router-dom"
import img from "../assets/image/images.png"
// var pageNotNull = [];
const navMain = [
    {
        display: 'Shop',
        path: '/shop'
    },
    {
        display: 'About Us',
        path: '/about-us'
    },
    {
        display: 'Contact Us',
        path: '/contact-us'
    },
]
const Header = () => {
    // const pages = usePages();

    // if (pages !== null) {
    //     pages.map( item => (
    //       pageNotNull = item
    //     ))
    //     return (
    //         <ul>
    //             {
    //                 pageNotNull.sort((a,b)=>a.pageId > b.pageId ? 1 : -1).map(item => (
    //                     <li key={item.pageId}><Link to={item.uri}>{item.title}</Link></li>
    //                 ))
    //             }
    //         </ul>
    //     )
    // }

    //Show form search
    const [show, setShow] = useState(false);

    const showForm = e => {
        setShow(current => !current);
    }

    //Shrink header

    //Active menu
    const { pathname } = useLocation();
    const isActive = navMain.findIndex(e => e.path === pathname);
    const menuActive = useRef(null);
    const menuToggle = () => {
        menuActive.current.classList.toggle('active');
    }
    return (
        <header>
            <div className="container">
                <div className='header-layout'>
                    <div className='menu-bar'>
                        <i className='bx bx-menu'></i>
                    </div>
                    <div className="header-layout-logo">
                        <Link to={'/'}><img src={img} alt="Dragon Store" style={{borderRadius: "50%"}}/></Link>
                    </div>
                    <div className='header-layout-menu' ref={menuActive}>
                        <nav className="nav-bar">
                            <ul>
                                {
                                    navMain.map((item,index) => (
                                        <li key={index} onClick={menuToggle} className={`menu-header-item ${index === isActive ? 'active': ''}`}>
                                            <Link to={item.path}>{item.display}</Link>
                                        </li>
                                    ))
                                }
                                {/* <li onClick={menuToggle} className={`${isActive ? 'active': ''}`}><Link to={'/about-us'}>About Us</Link></li>
                                <li onClick={menuToggle} className={`${isActive ? 'active': ''}`}><Link to={'/contact-us'}>Contact Us</Link></li> */}
                            </ul>
                        </nav>
                        <div className='menu-close'>
                            <i className='bx bx-x'></i>
                        </div>
                    </div>
                    <div className="header-items">
                        <div className='search'>
                            <form className={`${show ? 'active': 'hidden'}`} autoComplete="true">
                                <input type="search" name='s'/>
                                {/* <input type="submit" value="Search" /> */}
                            </form>
                            <button type="button" onClick={showForm} className={`${show ? 'active': 'hidden'}`}> <i className='bx bx-x'></i> </button>
                            <button type="button" onClick={showForm} className={`${show ? 'hidden': 'active'}`}> <i className='bx bx-search'></i> </button>
                        </div>
                        <Link to="/cart"><i className='bx bx-cart'></i></Link>
                    </div>
                </div>
            </div>
        </header>
    )
}

export default Header
