import axios from "axios";
import { fetchDataRequest, fetchDataSuccess, fetchDataError } from "./action";

const PRODUCTS_API = `
{
  products {
    nodes {
      ... on VariableProduct {
        id
        name
      }
      ... on ExternalProduct {
        id
        name
      }
      ... on GroupProduct {
        id
        name
      }
      ... on SimpleProduct {
        id
        name
        uri
        slug
        sku
        shortDescription(format: RENDERED)
        salePrice(format: RAW)
        shippingClassId
        shippingRequired
        shippingTaxable
        reviewCount
        reviews {
          averageRating
          edges {
            rating
          }
        }
        regularPrice(format: FORMATTED)
        price(format: FORMATTED)
        onSale
        link
        featuredImageDatabaseId
        description(format: RAW)
        date
        dateOnSaleTo
        dateOnSaleFrom
        averageRating
        manageStock
        image {
          sourceUrl(size: MEDIUM)
        }
      }
    }
    pageInfo {
      hasNextPage
      hasPreviousPage
    }
  }
}
`
export function fetchProducts() {
    return dispatch => {
        dispatch(fetchDataRequest());
        axios("http://localhost/wordpress/graphql/", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            data: JSON.stringify({ query: PRODUCTS_API })
        }).then(response => {
            dispatch(fetchDataSuccess(response.data.data.products.nodes));
        }).catch(error => {
            dispatch(fetchDataError(error));
        });
    };
}
