import React from 'react'
import usePages from '../data/usePages'
import { useLocation } from "react-router-dom"
import prase from "html-react-parser"

var pageNotNull = [];
var isFrontPage, pageTitle, pageContent,pageUri = '';
const Home = () => {
    const location = useLocation();
    const pages = usePages();
    const location_path = location.pathname;
    const path = location_path;

    if (pages !== null) {
        pages.map( item => (
          pageNotNull = item
        ))
        pageNotNull.map( item => (
            (
            pageTitle = item.title,
            isFrontPage = item.isFrontPage,
            pageContent = item.content,
            pageUri = item.uri
            )
        ))
            return (
                <div className='page-wrapper'>
                    <div className='container'>
                        <div className='title-page-wrapper'>
                            <h2>{pageTitle}</h2>
                        </div>
                        <div className='content-page-wrapper'>
                            <div className='content-first'>{prase(pageContent)}</div>
                        </div>
                    </div>
                </div>
            )
    }

}

export default Home
