import {useState, useEffect} from 'react'

const PRODUCTS_API = `
{
  products {
    nodes {
      ... on VariableProduct {
        id
        name
      }
      ... on ExternalProduct {
        id
        name
      }
      ... on GroupProduct {
        id
        name
      }
      ... on SimpleProduct {
        id
        name
        uri
        slug
        sku
        shortDescription(format: RENDERED)
        salePrice(format: RAW)
        shippingClassId
        shippingRequired
        shippingTaxable
        reviewCount
        reviews {
          averageRating
          edges {
            rating
          }
        }
        regularPrice(format: FORMATTED)
        price(format: FORMATTED)
        onSale
        link
        galleryImages {
          nodes {
            id
            link
            uri
          }
        }
        featuredImageDatabaseId
        featured
        description(format: RAW)
        date
        dateOnSaleTo
        dateOnSaleFrom
        averageRating
        manageStock
      }
    }
    pageInfo {
      hasNextPage
      hasPreviousPage
    }
  }
}
`
const url_api = "http://localhost/wordpress/graphql/";

const useProducts = () => {
    const [products, setProducts] = useState([]);
    useEffect( (url_api)=> {
        fetch(url_api, {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ query: PRODUCTS_API })
          }).then(response => response.json())
            .then(res => setProducts(res.data.products.nodes))
        }, [url_api]);
    return [products];
}

export default useProducts
