import React from 'react'
import usePages from '../data/usePages'

import { useLocation } from "react-router-dom"

var pageNotNull = [];
var pageUri = '';
const Home = () => {
    const location = useLocation();
    const pages = usePages();
    const location_path = location.pathname;

    if (pages !== null) {
        pages.map( item => (
          pageNotNull = item
        ))
        pageNotNull.map( item => (
            pageUri = item.uri
        ))
    }

    if (pageUri === location_path ){
        return (
            <div className='container'>
                {
                    pageNotNull.map(item => (
                        <p key={item.id}>{item.title}</p>
                    ))
                }
            </div>
        )
    }
}

export default Home
