import React from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom'

const Slider = props => {
    const data = props.data;
    if (data !== undefined) {
        return (
            data.map((item, index) => (
                <SliderItem key={index} item={item} />
            ))
        )
    }
}

Slider.propTypes = {
    data: PropTypes.array
}

const SliderItem = props => (
    <div className='slider-item' style={
        {
            'backgroundImage': `url(${props.item.img})`
        }
    }>
        <div className='container'>
            <div className='content-slider'>
                <div className='row slider-text'>
                    <div className='col-sm-12 '>
                        <h1 className='title-slider'>{props.item.title}</h1>
                        <h2 className='subtitle-slider'>{props.item.subtitle}</h2>
                        <div className='view-detail'>
                            <Link to={props.item.path}>View Detail</Link>
                            <i class='bx bx-chevron-right'></i>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
)

export default Slider
