import React from 'react'
import { useParams } from 'react-router-dom'
import useProducts from '../data/useProducts'

const ProductDetail = () => {
  const { slug } = useParams();
  const products = useProducts();
  var product = [];
  products.map(item => (
    product = item
  ))
  const thisProduct = product.find((product) => String(product.slug) === slug) || {};
  return (
    <div className='container'>
      <div className='page-detail'>
        <div className='img'>
          <img src="" alt="" />
          {
             thisProduct.map(item => (
              item.name
            ))
          }
        </div>
        <div className='content'>
          <h2 className='title'>{thisProduct.name}</h2>
          <p className='description'>{thisProduct.description}</p>
          <span className='price'>{thisProduct.price}</span>
          <span className='rating'>{thisProduct.averageRating}</span>
        </div>
      </div>
    </div>
  )
}

export default ProductDetail
