import React from 'react'
import usePages from '../data/usePages'

import { useLocation } from "react-router-dom"

var pageNotNull = [];
var pageUri, pageTitle, pageContent = '';
const Home = () => {
    const location = useLocation();
    const pages = usePages();
    const location_path = location.pathname;
    const path = location_path;

    if (pages !== null) {
        pages.map( item => (
          pageNotNull = item
        ))
        pageNotNull.map( item => (
            (
            pageTitle = item.title,
            pageUri = item.uri,
            pageContent = item.description
            )
        ))
        if (pageUri === location_path || path === location_path){
            return (
                <div className='page-wrapper'>
                    <div className='container'>
                        <div className='title-page-wrapper'>
                            <h2>{pageTitle}</h2>
                        </div>
                        <div className='content-page-wrapper'>
                            <p>{pageContent}</p>
                        </div>
                    </div>
                </div>
            )
        }
    }

}

export default Home
