import React from 'react'

import useCategoriesProduct from '../../data/useCategoriesProduct'

let categoriesData = [];
const CategoryBanner = () => {
    const categories = useCategoriesProduct();

    categories.map(item => (
        categoriesData = item
    ))
    // console.log(categoriesData);
    if (categoriesData.length > 0) {
        return (
            <div className='category-layout'>
                <div className='container'>
                    <div className='row'>
                        <div className='col-md-8'>
                            <div className='row'>
                                {
                                    categoriesData.map(item => (
                                        item.slug === "fruits" ? (
                                            <div className='col-md-6' key={item.slug}>
                                                {
                                                    item.slug === 'fruits' ? (
                                                        <div className={item.slug}>{item.name}</div>
                                                    ) : item.slug === 'vegetables' ? (
                                                        <div className={item.slug}>{item.name}</div>
                                                    ) : null
                                                }
                                            </div>
                                        ) : null
                                    ))
                                }
                                {
                                    categoriesData.map(item => (
                                        item.slug === "vegetables-2" ? (
                                            <div className='col-md-6' key={item.slug}>
                                                <p>{item.name}</p>
                                            </div>
                                        ) : null
                                    ))
                                }
                            </div>
                        </div>
                        <div className='col-md-4'>
                            {
                                categoriesData.map(item => (
                                    item.slug === "juices" ? (
                                        <div className='juices' key={item.slug}>
                                            {item.name}
                                        </div>
                                    ):null
                                ))
                            }
                            {
                                categoriesData.map(item => (
                                    item.slug === "dried" ? (
                                        <div className={`category-${item.slug}`} key={item.slug}>
                                            {item.name}
                                        </div>
                                    ):null
                                ))
                            }
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}

export default CategoryBanner
