import React from 'react'
import PropTypes from 'prop-types'
import { Link } from 'react-router-dom';
import HTMLReactParser from 'html-react-parser';

const ProductsLoadMore = props => {
    const data = props.data;
    if (data.length > 0) {
      return (
        <div className='featured-product'>
            <div className='container'>
              <div className='heading-section'>
                <span className='subheading'></span>
                <h2>Featured Products</h2>
                <p></p>
              </div>
              <div className='featured-product-content'>
                <div className='row'>
                  {
                    data.slice(0, props.visable).map((item, index) => (
                      <div className='col-md-6 col-lg-3' key={index}>
                        <div className='product'>
                          <Link to={`shop/${item.slug}`}>
                            <div className='img-pod'>
                              <img src={item.image.sourceUrl} alt="" />
                              {
                                item.onSale === true ? (
                                  <span className='sale'>
                                    <ins>{HTMLReactParser(item.regularPrice)}</ins>
                                    <del>{HTMLReactParser(item.salePrice)}</del>
                                  </span>
                                ) : (
                                  <span className='price'>
                                    {HTMLReactParser(item.regularPrice)}
                                  </span>
                                )
                              }
                            </div>
                          </Link>
                        </div>
                      </div>
                    ))
                  }
                </div>
              </div>
            </div>
        </div>
      )
    }
}

ProductsLoadMore.propTypes = {
    data: PropTypes.array.isRequired,
    visable: PropTypes.number.isRequired,
    error: PropTypes.bool,
    loading: PropTypes.bool
}

export default ProductsLoadMore
