import React from 'react'
import useMenus from '../data/useMenus'
import { ReactMegaMenu } from 'react-mega-menu/lib';


const MenusPrimary = () => {
    var menuData = '';
    var menuItemPrimary = [];
    const menusInfo = useMenus();
    menusInfo.map(item => (
        menuData = item
    ))
    if (menuData.length > 0 ) {
        menuData.map((item,index) => (
            item.locations == 'PRIMARY' ? menuItemPrimary[`${index}`] = item.menuItems.nodes : null
        ))
    }
    if (menuItemPrimary.length > 0) {
        return (
            <ReactMegaMenu data={menuItemPrimary} />
        )
    }
}

export default MenusPrimary
