import React, { useState } from 'react'
// import usePages from '../data/usePages';

import { Link } from "react-router-dom"
import img from "../assets/image/images.png"
// var pageNotNull = [];
const Header = () => {
    // const pages = usePages();

    // if (pages !== null) {
    //     pages.map( item => (
    //       pageNotNull = item
    //     ))
    //     return (
    //         <ul>
    //             {
    //                 pageNotNull.sort((a,b)=>a.pageId > b.pageId ? 1 : -1).map(item => (
    //                     <li key={item.pageId}><Link to={item.uri}>{item.title}</Link></li>
    //                 ))
    //             }
    //         </ul>
    //     )
    // }
    const [show, setShow] = useState(false);

    const showForm = e => {
        setShow(current => !current);
    }
    return (
        <header>
            <div className="container">
                <div className='header-layout'>
                    <div className='menu-bar'>
                        <i className='bx bx-menu'></i>
                    </div>
                    <div className="header-layout-logo">
                        <Link to={'/'}><img src={img} alt="Dragon Store" style={{borderRadius: "50%"}}/></Link>
                    </div>
                    <div className='header-layout-menu'>
                        <nav className="nav-bar">
                            <ul>
                                <li><Link to={'/shop'}>Shop</Link></li>
                                <li><Link to={'/about-us'}>About Us</Link></li>
                                <li><Link to={'/contact-us'}>Contact Us</Link></li>
                            </ul>
                        </nav>
                        <div className='menu-close'>
                            <i className='bx bx-x'></i>
                        </div>
                    </div>
                    <div className="header-items">
                        <div className='search'>
                            <form className={`${show ? 'active': 'hidden'}`} autoComplete="true">
                                <input type="search" name='s'/>
                                {/* <input type="submit" value="Search" /> */}
                            </form>
                            <button type="button" onClick={showForm} className={`${show ? 'active': 'hidden'}`}> <i className='bx bx-x'></i> </button>
                            <button type="button" onClick={showForm} className={`${show ? 'hidden': 'active'}`}> <i className='bx bx-search'></i> </button>
                        </div>
                        <Link to="/cart"><i className='bx bx-cart'></i></Link>
                    </div>
                </div>
            </div>
        </header>
    )
}

export default Header
