import React from 'react'
import usePages from '../data/usePages'

import { useLocation } from "react-router-dom"

var pageNotNull = [];
var pageUri, pageTitle = '';
const Home = () => {
    const location = useLocation();
    const pages = usePages();
    const location_path = location.pathname;
    const path = location_path;

    if (pages !== null) {
        pages.map( item => (
          pageNotNull = item
        ))
        pageNotNull.map( item => (
            (
            pageTitle = item.title,
            pageUri = item.uri
            )
        ))
        if (pageUri === location_path && path === location_path){
            return (
                <div className='container'>
                    <p>{pageTitle}</p>
                </div>
            )
        }
    }

}

export default Home
