import React , {useState, useEffect } from 'react'
// import usePages from '../data/usePages'
import { useLocation } from "react-router-dom"
// import prase from "html-react-parser"

// var pageNotNull = [];

var location_path = '';
const PAGE_HOME = `
($uri: String = location_path) {
    pageBy(uri: $uri) {
      id
      content(format: RENDERED)
      link
      uri
      title(format: RENDERED)
      slug
      pageId
    }
  }
`

const Home = () => {
    const location = useLocation();
    location_path = location.pathname;

    const [home, setHome] = useState([])
    useEffect(()=> {
        fetch("http://localhost/wordpress/graphql/", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({ query: PAGE_HOME })
          }).then(response => response.json())
            .then(home => setHome(home.data.pageBy))
        }, []);
    return (
        <div>
            {
                JSON.stringify(home)
            }
        </div>
    )
}

export default Home
