import React from 'react'
import testimonies from '../../assets/fake-data/testimonies'

var lol = [];
var data = [];
const Testimony = () => {
    testimonies.map(item => (
        lol = item.content
    ))
    lol.map(item => (
        data = item
    ))
    console.log(testimonies);
}

export default Testimony
