import axios from 'axios';
import {useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux/es/exports';
import { setProducts } from '../redux/action/productActions';

const PRODUCTS_API = `
{
  products {
    nodes {
      ... on VariableProduct {
        id
        name
      }
      ... on ExternalProduct {
        id
        name
      }
      ... on GroupProduct {
        id
        name
      }
      ... on SimpleProduct {
        id
        name
        uri
        slug
        sku
        shortDescription(format: RENDERED)
        salePrice(format: RAW)
        shippingClassId
        shippingRequired
        shippingTaxable
        reviewCount
        reviews {
          averageRating
          edges {
            rating
          }
        }
        regularPrice(format: FORMATTED)
        price(format: FORMATTED)
        onSale
        link
        featuredImageDatabaseId
        description(format: RAW)
        date
        dateOnSaleTo
        dateOnSaleFrom
        averageRating
        manageStock
        image {
          sourceUrl(size: MEDIUM)
        }
      }
    }
    pageInfo {
      hasNextPage
      hasPreviousPage
    }
  }
}
`
// const url_api = "http://localhost/wordpress/graphql/";

const useProducts = () => {
    const products = useSelector((state) => state);
    const dispatch = useDispatch();
    const fetchProducts = async () => {
      const response = await axios
      .get("http://localhost/wordpress/graphql/")
      .catch((err) => {
        console.log(err);
      });
      dispatch(setProducts(response.data))
    }
    useEffect( () => {
      return fetchProducts();
    }, []);
    // console.log("Products: ", products)
    // useEffect( () => {
    //     fetch("http://localhost/wordpress/graphql/", {
    //         method: "POST",
    //         headers: { "Content-Type": "application/json" },
    //         body: JSON.stringify({ query: PRODUCTS_API })
    //       }).then(response => response.json())
    //         .then(products => setProducts(products.data.products.nodes))
    //     }, []);
    // return [products];
}

export default useProducts
