import React, {useEffect, useState} from 'react'
import axios from 'axios'
import { useSelector, useDispatch } from 'react-redux'
import {setProducts} from '../redux/action/productActions'

const PRODUCTS_API = `
{
  products {
    nodes {
      ... on VariableProduct {
        id
        name
      }
      ... on ExternalProduct {
        id
        name
      }
      ... on GroupProduct {
        id
        name
      }
      ... on SimpleProduct {
        id
        name
        uri
        slug
        sku
        shortDescription(format: RENDERED)
        salePrice(format: RAW)
        shippingClassId
        shippingRequired
        shippingTaxable
        reviewCount
        reviews {
          averageRating
          edges {
            rating
          }
        }
        regularPrice(format: FORMATTED)
        price(format: FORMATTED)
        onSale
        link
        featuredImageDatabaseId
        description(format: RAW)
        date
        dateOnSaleTo
        dateOnSaleFrom
        averageRating
        manageStock
        image {
          sourceUrl(size: MEDIUM)
        }
      }
    }
    pageInfo {
      hasNextPage
      hasPreviousPage
    }
  }
}
`
const TestRedux = () => {
    const products = useSelector((state) => state);
    const dispatch = useDispatch();
    
    const fetchData = async () => {
        const response = await axios("http://localhost/wordpress/graphql/", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            data: JSON.stringify({ query: PRODUCTS_API })
          }).then(response => response.data.data.products.nodes);
        // console.log(dispatch(setProducts(response)));
        return setProducts(response);
    };

    useEffect( () => {
        dispatch(fetchData());
    }, []);
    console.log(products);
  return (
    <div>TestRedux</div>
  )
}

export default TestRedux
