import React, {Component} from "react";
import {connect} from "react-redux";
import { add, subtract, removeItemFromCart } from "../redux/actions/action";

import { fetchProducts } from "../../redux/actions/fetchData";

class Cart extends Component {
    componentDidMount() {
        this.props.dispatch(fetchProducts());
    }

    add = (e, itemname) => {
        this.props.dispacth(add(itemname));
    };

    subtract = (e, itemname) => {
        this.props.dispatch(subtract(itemname));
    };
    
    removeItemFromCart = (e, itemname, amount) => {
        console.log("amount", amount);
        this.props.dispatch(removeItemFromCart(itemname, amount));
    };

    render(){
        const { cart } = this.props;
        return (
            console.log(cart)
        )
    }
}

const mapStateToProps = state => ({
    item: state.products.item,
    cart: state.products.cart,
    total: state.products.total
});

export default connect(mapStateToProps)(Cart)
