import { actionTypes } from "../contanst/actionTypes";

const initialState = {
    products: []
}
export const productReducer = (state = initialState, {type, payload}) => {
    switch (type) {
        case actionTypes.SET_PRODUCTS:
            if (payload !== null) {
                return {...state, payload: products};
            }
            break;
        default:
            return state;
    }
}
