import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import DropDown from './MenuDropdown/DropDown'

export const menuPrimary = [
    {
        display: 'Home',
        path: '/',
        slug: 'home'
    },
    {
        display: 'Shop',
        path: '/shop',
        slug: 'shop',
        submenu: [
            {
                display: 'Wishlist',
                path: '/wishlist',
                slug: 'wishlist'
            },
            {
                display: 'Cart',
                path: '/cart',
                slug: 'cart'
            },
            {
                display: 'Checkout',
                path: '/checkout',
                slug: 'checkout'
            },
        ]
    },
    {
        display: 'About',
        path: '/about',
        slug: 'about'
    },
    {
        display: 'Blog',
        path: '/blog',
        slug: 'slug'
    },
    {
        display: 'Contact Us',
        path: '/contact-us',
        slug: 'contact-us'
    },
]

const MenusPrimary = () => {
    // set dropdown menu
    const [dropdown, setDropdown] = useState(false);
    const showSubMenu = (e) => {
        e.preventDefault();
        setDropdown((prev) => !prev)
    }
    return (
        <nav className="navbar navbar-expand-lg navbar-dark ftco_navbar bg-dark ftco-navbar-light" id="ftco-navbar">
            <div className="container">
                <Link to={'/'} className="navbar-brand">Vegefoods</Link>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#ftco-nav"
                    aria-controls="ftco-nav" aria-expanded="false" aria-label="Toggle navigation">
                    <span className='bx bx-menu'></span> Menu
                </button>

                <div className="collapse navbar-collapse" id="ftco-nav">
                    <ul className="navbar-nav ml-auto">
                        {
                            menuPrimary.map((item, index) => (
                                item.submenu ? (
                                    <>
                                    <li className='nav-item dropdown' key={index}>
                                        <Link to={item.path} className='nav-link' id="dropdown04" data-toggle="dropdown"
							            aria-haspopup="true" aria-expanded={dropdown ? 'true' : 'false'} onMouseEnter={showSubMenu}>
                                            {item.display}
                                        </Link>
                                        <DropDown submenu={item.submenu} dropdown={dropdown} />
                                    </li>
                                    </>
                                ) : (
                                    <li className='nav-item' key={index}><Link to={item.path} className='nav-link'>{item.display}</Link></li>
                                )
                            ))
                        }
                        {/* <li className="nav-item dropdown">
                            <a className="nav-link dropdown-toggle" href="#" id="dropdown04" data-toggle="dropdown"
                                aria-haspopup="true" aria-expanded="false">Shop</a>
                            <div className="dropdown-menu" aria-labelledby="dropdown04">
                                <a className="dropdown-item" href="shop.html">Shop</a>
                                <a className="dropdown-item" href="wishlist.html">Wishlist</a>
                                <a className="dropdown-item" href="product-single.html">Single Product</a>
                                <a className="dropdown-item" href="cart.html">Cart</a>
                                <a className="dropdown-item" href="checkout.html">Checkout</a>
                            </div>
                        </li> */}
                        <li className="nav-item cta cta-colored">
                            <Link to="/cart" className='cart-icon'>
                                <i className='bx bx-cart'></i>
                                <span className='number-cart'>0</span>
                            </Link>
                        </li>

                    </ul>
                </div>
            </div>
        </nav>
    )

}

export default MenusPrimary
