import React from 'react'
import useProducts from '../data/useProducts'
import HTMLReactParser from 'html-react-parser';

var productsNotNull = [];
const Products = () => {
    const products = useProducts()
    products.map(item => (
        productsNotNull = item
    ))
    if (productsNotNull !== null) {
        return (
            <div className="row">
                {
                    productsNotNull.map(item => (
                        <div className='product-card col-sm-4' key={item}>
                            <div className='product-card-img'>
                                <img src={item.image.sourceUrl !== null ? item.image.sourceUrl : ''} alt="" />
                            </div>
                            <div className='product-card-content'>
                                <h3>{item.name}</h3>
                                <p>{item.description}</p>
                            </div>
                            <div className='product-card-bootom'>
                                <div className='price-and-rate'>
                                    <div className='price'>
                                        {HTMLReactParser(item.price)}
                                    </div>
                                    <div className='rate'>
                                        {item.reviews.averageRating}
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))
                }
            </div>
        );
    }
}

export default Products
